select Concurso.id, Concurso.ano, Concurso.area, Concurso.especialidade,        Cargo.nome as cargoNome, Cargo.sigla as cargoSigla, Orgao.nome as orgaoNome, Orgao.sigla as orgaoSigla, Edital.prazoInscricao, 		   Imagem.uuid as orgaoUuid     from Concurso     inner join Cargo on Concurso.cargo_id = Cargo.id     inner join Edital on Concurso.edital_id = Edital.id     inner join Orgao on Edital.orgao_id = Orgao.id     left outer join Imagem on Imagem.id = Orgao.logo_id  where  	 Cargo.cargoOculto = false and Orgao.orgaoOculto = false AND     ((upper(to_ascii(Orgao.nome)) like upper(to_ascii(?)) or upper(to_ascii(Orgao.sigla)) like upper(to_ascii(?)) or Orgao.id in (select orgao_id from ApelidoOrgao where upper(to_ascii(apelido)) like upper(to_ascii(?))))		or     (upper(to_ascii(Cargo.nome)) like upper(to_ascii(?)) or upper(to_ascii(Cargo.sigla)) like upper(to_ascii(?)) or Cargo.id in (select cargo_id from ApelidoCargo where upper(to_ascii(apelido)) like upper(to_ascii(?))))) order by Concurso.ano desc, upper(Orgao.sigla), upper(Cargo.sigla), Concurso.id desc limit ?